catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue === '') {
        g_form.hideFieldMsg('gst_number', true);
        return;
    }

    // Let the user know we are checking
    g_form.showFieldMsg('gst_number', 'Verifying GST number...', 'info');

    var ga = new GlideAjax('GSTValidator');
    ga.addParam('sysparm_name', 'validateGSTNumber');
    ga.addParam('sysparm_gst_number', newValue);
    ga.getXMLAnswer(function(answer) {
        // Hide the "Verifying..." message
        g_form.hideFieldMsg('gst_number', true);

        if (answer === 'false') {
            g_form.showFieldMsg('gst_number', 'This GST number is not valid. Please check and re-enter.', 'error');
        } else {
            g_form.showFieldMsg('gst_number', 'GST number verified.', 'ok');
        }
    });
}
