sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {

    var emailField = 'contact_email'; 

    if (isLoading || newValue === '') {
        g_form.hideFieldMsg(emailField, true); 
        return;
    }

    var ga = new GlideAjax('VendorUtils');
    ga.addParam('sysparm_name', 'validateVendorEmail');
    ga.addParam('sysparm_email', newValue);
    ga.getXMLAnswer(function(answer) {
        var isDuplicate = answer;

        if (isDuplicate === 'true') {
            g_form.showFieldMsg(
                emailField, 
                'This email address is already registered to another vendor.',
                'error'
            );
        } else {
            g_form.hideFieldMsg(emailField, true); 
        }
    });
}
