sys_script_include.script:
var VendorUtils = Class.create();
VendorUtils.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    validateVendorEmail: function() {
        var email = this.getParameter('sysparm_email');
        if (!email) {
            return 'false';
        }

        var vendor = new GlideRecord('x_1821895_vendor_a_vendor');
        vendor.addQuery('contact_email', email); 
        vendor.setLimit(1);
        vendor.query();

        if (vendor.hasNext()) {
            return 'true';
        }
        return 'false';
    },

    type: 'VendorUtils'
});
