sys_script_include.script:
var GSTValidator = Class.create();
GSTValidator.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    validateGSTNumber: function() {
        var gstin = this.getParameter('sysparm_gst_number');
        // Your API key is now directly in the REST Message Endpoint.
        // gs.info("GST_VALIDATION_DEBUG: Starting direct validation for GSTIN: " + gstin); // Debug log

        if (!gstin) {
            return 'false';
        }

        try {
            // Call the REST Message using the name and HTTP Method
            var restMsg = new sn_ws.RESTMessageV2('GSTIN Check Verification', 'Check GST Number');

            // Pass the GSTIN to substitute the ${gstin_number} in the main REST Message Endpoint
            restMsg.setStringParameterNoEscape('gstin_number', gstin);

            var response = restMsg.execute();
            var httpStatus = response.getStatusCode();
            var responseBody = response.getBody(); 

            // gs.info("GST_VALIDATION_DEBUG: HTTP Status: " + httpStatus); // Debug log
            // gs.info("GST_VALIDATION_DEBUG: Response Body: " + responseBody); // Debug log

            if (httpStatus == 200) {
                var responseObj = JSON.parse(responseBody);

                if (responseObj.flag === true) {
                    // gs.info("GST_VALIDATION_DEBUG: Validation PASSED."); // Debug log
                    return 'true'; 
                } else if (responseObj.flag === false) {
                    // gs.info("GST_VALIDATION_DEBUG: Validation FAILED. API Message: " + responseObj.message); // Debug log
                    return 'false'; 
                }
            }
            // gs.info("GST_VALIDATION_DEBUG: Validation FAILED. Non-200 HTTP status or unexpected response."); // Debug log
            return 'false';

        } catch (ex) {
            // gs.error('GST_VALIDATION_DEBUG: An error occurred during validation: ' + ex.getMessage()); // Debug log
            return 'false';
        }
    },

    type: 'GSTValidator'
});
