sys_script_include.script:
var GSTValidator = Class.create();
GSTValidator.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    validateGSTNumber: function() {
        var gstin = this.getParameter('sysparm_gst_number');
        if (!gstin) {
            return 'false';
        }

        try {
            // Use the REST Message we already created
            var restMsg = new sn_ws.RESTMessageV2('GSTIN Check Verification', 'Verify GSTIN');
            restMsg.setStringParameterNoEscape('gstin', gstin);

            var response = restMsg.execute();
            var httpStatus = response.getStatusCode();

            if (httpStatus == 200) {
                var responseObj = JSON.parse(response.getBody());
                // The API returns a 'flag' field which is true for a valid GSTIN
                if (responseObj.flag === true) {
                    return 'true'; // It's valid
                }
            }
            // If status is not 200 or flag is not true, it's invalid
            return 'false';

        } catch (ex) {
            gs.error('GST Validation failed: ' + ex.getMessage());
            return 'false';
        }
    },

    type: 'GSTValidator'
});
