sys_ux_client_script.script:
/**
* @param {params} params
* @param {api} params.api
* @param {any} params.event
* @param {any} params.imports
* @param {ApiHelpers} params.helpers
*/
function handler({api, event, helpers, imports}) {
    const hour = new Date().getHours();
    let greeting = '';

    if(hour < 12){
        greeting = 'Good Morning';
    }
    else if(hour < 17){
        greeting = 'Good Afternoon';

    }
    else{
        greeting = 'Good Evening'
    }

    const username = api.context.session.user.firstName;

    const personalised_Greetings = `${greeting}, ${username}`;

    api.setState('GreetingMessage',personalised_Greetings)



}
